mapper.select:
SELECT * FROM place.placetbl


		p_location = #{p_location}



			AND

		p_thema = #{p_thema}




		1=1

mapper.update:
UPDATE place.placetbl
SET p_views=p_views + 1
WHERE p_id=#{p_id}

mapper.select:
SELECT p_id, p_name, p_location, p_stScore, p_iname
FROM place.placetbl
WHERE 1=1

	AND (

		p_location LIKE CONCAT('%', #{region}, '%')

	)


	AND (

		p_name LIKE CONCAT('%', #{subregion}, '%')

	)


	AND (

		p_thema = #{theme}

	)
